import React, { useState } from "react";
import styled from "styled-components";
import background from "@/assets/img/background/silkBackground.jpg";
import TarotCard from "@/components/tarotCard";
import tarotCardFront from "@/assets/img/tarotCard/tarotCardFront.png";
import tarotCardBack from "@/assets/img/tarotCard/tarotCardBack.png";
import HashTag from "@/components/hashtag";
import TextBubble from "@/components/textBubble";
import { StyledTextBubbleProps } from "@/types/luckType";
import GhostModal from "@/components/ghostModal";

const PageContainer = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: url(${background});
  background-size: cover;
  background-attachment: scroll;
`;

const Backdrop = styled.div<{ isOpen: boolean }>`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8); /* Semi-transparent black background */
  display: ${(props) => (props.isOpen ? "block" : "none")};
  z-index: 1;
`;

const ContentContainer = styled.div`
  display: flex;
  justify-content: center; /* ÏàòÌèâ Ï§ëÏïô Ï†ïÎ†¨ */
  align-items: center; /* ÏàòÏßÅ Ï§ëÏïô Ï†ïÎ†¨ */
  flex-direction: row; /* ÏàòÌèâ Ï†ïÎ†¨Î°ú Î≥ÄÍ≤Ω */
  height: 100%;
  width: 100%;
`;

const CardContainer = styled.div`
  padding: 70px 55px;
`;

const Title = styled.div`
  font-size: 60px;
  color: white;
  font-family: "Brodies";
`;

const TitleContainer = styled.div`
  padding: 0px 0px 0px 55px;
  text-align: left;
`;

const HashtagContainer = styled.div`
  display: flex;
`;

const ContentWrapper = styled.div`
  display: flex;
  flex-direction: column;
  height: 100%;
  padding: 25px 0px 25px 0px;
`;

const TextBubbleContainer = styled.div`
  margin-bottom: 10px; /* Í∞Å ÌÖçÏä§Ìä∏ Î≤ÑÎ∏î ÏÇ¨Ïù¥Ïùò Í∞ÑÍ≤© Ï°∞Ï†ï */
`;

const ModalContainer = styled.div<{ isOpen: boolean }>`
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  padding: 20px;
  display: ${(props) => (props.isOpen ? "block" : "none")};
  z-index: 3;
`;

const NextSpendPage: React.FC = () => {
  const [isModalOpen, setIsModalOpen] = useState(false); // Î™®Îã¨ Ïó¥Î¶º Ïó¨Î∂Ä ÏÉÅÌÉú

  // Î™®Îã¨ Ïó¥Í∏∞ Ìï®Ïàò
  const toggleModal: () => void = () => {
    setIsModalOpen(!isModalOpen);
  };

  // Î™®Îã¨ Ïó¥Í∏∞ Ìï®Ïàò
  const openModal = () => {
    setIsModalOpen(true);
  };

  // Î™®Îã¨ Îã´Í∏∞ Ìï®Ïàò
  const closeModal = () => {
    setIsModalOpen(false);
  };

  const titleTextBubbleProps: StyledTextBubbleProps = {
    text: "üåü ÎßàÎÖÄ üåü",
    width: "136PX",
    background: "#4D1B2D80",
    opacity: "1",
    paddingLeftRight: "28px",
    paddingTopBottom: "15px",
    borderRadius: "10px",
    hoverable: false,
  };

  const contentTextBubbleProps: StyledTextBubbleProps = {
    text: `Î≥ÑÎì§Ïù¥ Ï†úÎ≤ï ÎßéÏù¥ Îñ¥ÎäîÍ±∏? Ïñ¥Îîî Î≥¥Ïûê......
    ÎßàÎ≤ï Í∞ôÏùÄ Î≥ÄÌôîÍ∞Ä ÏùºÏñ¥ÎÇ¨Íµ¨ÎÇò! üßô‚Äç‚ôÄÔ∏è Ï†ÄÎ≤àÏ£º ÎåÄÎπÑ Ï†ÑÏ≤¥Ï†ÅÏù∏ ÏÜåÎπÑÍ∏àÏï°Ïù¥ Ï§ÑÏñ¥Îì§ÏóàÏßÄÎßå, ÏùåÏãùÏóê ÎåÄÌïú ÏÜåÎπÑÎ•º ÎßéÏù¥ ÌñàÍµ¨ÎÇò
    Ïù¥Îü¨Ìïú Î≥ÄÌôîÍ∞Ä Î∞úÏÉùÌïú Ïù¥Ïú†Î•º ÏïåÏïÑÎ≥¥Í≥† Îçî ÎÇòÏùÄ Ïû¨Ï†ï ÏÉÅÌÉúÎ•º Ïú†ÏßÄÌï† Ïàò ÏûàÎäî Î∞©Î≤ïÏùÑ Í≥†ÎØºÌï¥Î≥¥Ïûê.
    ÎÑ§ ÎØ∏ÎûòÏùò ÏßÄÏ∂úÎèÑ ÏïåÍ≥† Ïã∂Îã§Í≥†? Ïñ¥Î†µÏßÄ ÏïäÏßÄ. ÏÜêÏùÑ Ï§òÎ≥¥Í≤†Îãà?
    ÏïÑÎãàÎ©¥, ÎÑ§ Ïπ¥ÎìúÎ•º ÏßÄÍ∏à Î∞îÎ°ú Îã§Î•∏ Ïù∏Í∞ÑÎì§ÏóêÍ≤å Î≥¥Ïó¨Ï§ÑÎûò?`,
    width: "650px",
    background: "#4D1B2D80",
    opacity: "1",
    paddingLeftRight: "40px",
    paddingTopBottom: "40px",
    borderRadius: "20px",
    hoverable: false,
  };

  const shareTextBubbleProps: StyledTextBubbleProps = {
    text: `üÉè ÎÇòÏùò Ïπ¥ÎìúÎ•º Îã§Î•∏ ÏÇ¨ÎûåÎì§ÏóêÍ≤å Í≥µÏú†ÌïúÎã§ üÉè`,
    width: "650px",
    background: "#4D1B2D80",
    opacity: "1",
    paddingLeftRight: "40px",
    paddingTopBottom: "20px",
    borderRadius: "20px",
    hoverable: true,
  };

  return (
    <PageContainer>
      <ContentContainer>
        <CardContainer>
          <TarotCard
            width="300px"
            height="402px"
            cardWidth="100%"
            cardSrc={tarotCardFront}
            imageSrc={tarotCardBack}
            bottomImageWidth="100%"
            text="Special Card"
            fontsize="20px"
          ></TarotCard>
        </CardContainer>
        <TitleContainer>
          <Title>Next Week&apos;s Tarot Card</Title>
          <HashtagContainer>
            <HashTag text="Food"></HashTag>
            <HashTag text="Food"></HashTag>
            <HashTag text="Food"></HashTag>
            <HashTag text="Food"></HashTag>
            <HashTag text="Food"></HashTag>
          </HashtagContainer>
          <ContentWrapper>
            <TextBubbleContainer>
              <TextBubble {...titleTextBubbleProps} />
            </TextBubbleContainer>
            <TextBubbleContainer>
              <TextBubble {...contentTextBubbleProps} />
            </TextBubbleContainer>
            <TextBubbleContainer onClick={openModal}>
              <TextBubble {...shareTextBubbleProps} />
            </TextBubbleContainer>
          </ContentWrapper>
        </TitleContainer>
        <ModalContainer isOpen={isModalOpen}>
          <GhostModal zIndex={"3"} toggleModal={toggleModal} />
        </ModalContainer>
      </ContentContainer>
      <Backdrop isOpen={isModalOpen} onClick={closeModal} />
    </PageContainer>
  );
};

export default NextSpendPage;
