  };

  const openSpecialModal = () => {
    setIsSpecialModalOpen(true);
  };

  // Î™®Îã¨ Îã´Í∏∞ Ìï®Ïàò
  const closeSpecialModal = () => {
    setIsSpecialModalOpen(false);
  };

  const handlePageChange = (page: number) => {
