  };

  const settingProfileImage = async () => {
    try {
      // Ïù¥Î¶ÑÍ≥º Ï†ÑÌôîÎ≤àÌò∏Î•º ÏÇ¨Ïö©ÌïòÏó¨ SMSÎ•º Î≥¥ÎÉÖÎãàÎã§.
      await profileImage.mutateAsync(challengeCard.card.id);
      toast.info("üÉè ÌîÑÎ°úÌïÑ Ïπ¥Îìú Îì±Î°ù ÏôÑÎ£å üÉè");
    } catch (error) {
      console.error("Ïù∏Ï¶ù ÏΩîÎìú Ï†ÑÏÜ° Ïã§Ìå®:", error);
    }
  };

  const crystalChallengeBubbleProps: StyledChallengeBubbleProps = {
    text: `ÏÇ¨Ïã§ ÎßêÎèÑ ÏïàÎêòÎäî Ï±åÎ¶∞ÏßÄÏ£† Í∑∏ÏπòÎßå Ïñ¥Ï©åÍ≤†ÏäµÎãàÍπå Ìï¥ÏïºÏßÄ`,
    width: "300px",
    background: "#4D1B2D80",
    opacity: "1",
    paddingLeftRight: "40px",
    paddingTopBottom: "20px",
    borderRadius: "20px",
    imgLink: ChallegeSuccess,
    titleText: "ÏÜåÎπÑÍ∏àÏï° 5ÎßåÏõê ÎÑòÏßÄ ÏïäÍ∏∞",
  };

  const formatSpecialDateString = (SpecialdateString: string) => {
    const date = new Date(SpecialdateString);
    const year = date.getFullYear();
    const month = date.getMonth() + 1; // ÏõîÏùÄ 0Î∂ÄÌÑ∞ ÏãúÏûëÌïòÎØÄÎ°ú 1ÏùÑ ÎçîÌï¥Ï§çÎãàÎã§.
    const formatSpecialDateString = `${year}.${month
      .toString()
      .padStart(2, "0")}`;
    return formatSpecialDateString;
  };

  if (specialId !== null) {
    setFormattedSpecialDate(
      formatSpecialDateString(challengeCard.card.createdDate)
    );
  }

  return (
    <PageContainer>
      <BookSection>
        <BookTitle>Collection Book</BookTitle>
        <Book onPageChange={handlePageChange} />
      </BookSection>
      <ContentSection>
        {currentPage === 0 && (
          <div style={{ marginBottom: "140px", marginTop: "30px" }}>
            {Object.keys(groupImagesByMonth()).map((month) => (
              <div key={month}>
                <BasicText>
                  {month}
                  <DcitBtn
                    text="Ïπ¥ÎìúÏ∂îÏ≤ú"
                    onClick={() => goCardRecommend(month)}
                  />
                </BasicText>
                <div
                  style={{
                    display: "grid",
                    gridTemplateColumns: "repeat(5, 1fr)",
                    marginTop: "2rem",
                    marginBottom: "40px",
                  }}
                >
                  {groupImagesByMonth()[month].map((item, index) => {
                    return (
                      <TarotCard
                        key={index}
                        width="140px"
                        height="200px"
                        cardWidth="100%"
                        cardSrc={frontcard}
                        imageSrc={item.imagePath}
                        bottomImageWidth="100%"
