  width: 50%;
  height: 100%;
  padding-left: 20px;
  margin-top: 5%;
  overflow: auto;
  margin-bottom: 300px;
`;

const SpecialHeader = styled.div`
  width: 100%;
  text-align: left;
  margin-left: 20px;
`;

const SpecialText = styled.div`
  display: flex;
  align-items: center;
  font-family: "Pyeongchangpeace";
  text-align: left;
  color: #d39090;
  font-size: 36px;
  margin-bottom: 10px;
`;

const BasicText = styled.div`
