import React, { useState } from "react";
import styled from "styled-components";
import LandingPage from "@/assets/img/LandingPageTest.jpg";
import LandingMainLogo from "@/assets/img/LandingMainLogo.png";
import FinanceLuckCards from "@/assets/img/FinanceLuckCards.png";
import CreditCards from "@/assets/img/CreditCards.png";
import Trophy from "@/assets/img/Trophy.png";
import Bottles from "@/assets/img/Bottles.png";
import CardFlip from "@/components/cardFlip";
import { useNavigate } from "react-router-dom";

<link rel="stylesheet" type="text/css" href="@/styles.font.css" />;

const ToTarotContext = `Í∑ÄÏó¨Ïö¥ Ïù∏Í∞Ñ ÏÜêÎãòÏù¥ ÏôîÎÑ§? Î≥¥ÏïÑÌïòÎãà Í≥†ÎØºÏù¥ ÏûàÎÇòÎ≥∏Îç∞...
ÏïÑÌïò, ÏïåÍ≤†Îã§! ÏöîÏ¶ò ÏÜåÎπÑÍ∞Ä ÎäòÏñ¥ Í±±Ï†ïÏù∏ Í±∞Íµ¨ÎÇò? Îæ∞Ï°±Ìïú ÏàòÎ•º Ï∞æÍ≥† Ïã∂Ïñ¥? üîÆ
Í∞ÑÎã®Ìïú Ìï¥Í≤∞Ï±ÖÏùÄ ÎÇòÏò§Í∏∞ Ïñ¥Î†µÏßÄÎßå...... Ïñ¥Ï©åÎ©¥ ÎÇ¥Í∞Ä ÎèÑÏõÄÏù¥ Îê† ÏßÄÎèÑ Î™®Î•¥ÏßÄ.
ÏïûÏúºÎ°ú Îß§Ï£º ÎÇòÏóêÍ≤å Î∞©Î¨∏ÌïòÎèÑÎ°ù ÌïòÎ†¥.
Ï†ïÎßêÎ°ú ÏäµÍ¥ÄÏùÑ Î∞îÍæ∏Í≥† Ïã∂Îã§Î©¥, Îß§Ï£º ÌÉÄÎ°úÎ•º Î≥¥Îü¨ Ïò§Îäî Í≤É Ï†ïÎèÑÎäî Í¥úÏ∞ÆÏßÄ ÏïäÎãà?
ÎÇ¥ ÌÉÄÎ°úÏ†êÏùÄ ÏâΩÍ≤å Î≥º Ïàò ÏûàÎäî Í≤å ÏïÑÎãàÎûÄÎã§.
ÎÑ§Í∞Ä ÏùºÏ£ºÏùº Í∞Ñ ÏÜåÎπÑÌïú ÎÇ¥Ïö©ÏùÑ ÏöîÏ†ïÎì§ÏóêÍ≤å ÏïåÏïÑÏò§ÎùºÍ≥† Ìï†Í≤å.
Í∑∏ ÎÇ¥Ïö©ÏùÑ ÎÇ¥Í∞Ä ÌôïÏù∏Ìïú ÌõÑÏóê ÎÑ§ ÌÉÄÎ°úÏ†êÏùÑ Î¥êÏ§Ñ Í±∞Ïïº.
Í∑∏Î¶ºÏùÑ Ïûò Í∑∏Î¶¨Îäî ÏöîÏ†ïÎèÑ ÏûàÎäîÎç∞, Í∑∏ ÏπúÍµ¨Í∞Ä Ïò§ÏßÅ ÎÑàÎßåÏùÑ ÏúÑÌïú ÌÉÄÎ°úÏπ¥ÎìúÎèÑ Í∑∏Î†§Ï§Ñ Í±∞ÎûÄÎã§.
ÌõÑÌõÑ, ÍΩ§ÎÇò Í∏∞ÎåÄÎêòÏßÄ ÏïäÎãà?`;

const RecommendContext = `ÏïÑÌïò, Ïù∏Í∞Ñ ÏπúÍµ¨Ïó¨! ÎÑ§Í∞Ä Ïì∞Îäî ÎèàÏùò Ïù¥ÏïºÍ∏∞Í∞Ä ÎÇ¥ ÎßàÎ≤ïÏùò Íµ¨Ïä¨ÏùÑ ÌÜµÌï¥ ÎÇòÌÉÄÎÇòÎÑ§.üîÆ‚ú®Íµ¨Ïä¨Ïóê ÎÇòÌÉÄÎÇú ÏÜåÎπÑÌå®ÌÑ¥ÏúºÎ°ú ÌäπÎ≥ÑÌïú Ïπ¥ÎìúÎ•º Ï∞æÏïÑÎÉàÎã®Îã§. Ïù¥ Ïπ¥ÎìúÎäî ÎÑàÏùò ÏßÄÏ∂úÏóê Îî∞Î•∏ Ïû•Ï†êÏùÑ ÏµúÎåÄÌïú ÌôúÏö©Ìï¥, ÎÑàÏùò Îèà Ï£ºÎ®∏ÎãàÎ•º ÎçîÏö± ÌíçÏöîÎ°≠Í≤å ÎßåÎì§Ïñ¥ Ï§Ñ Í≤ÉÏù¥Ïïº.Í∑∏Î¶¨Í≥† Í∑∏ Ïπ¥ÎìúÍ∞Ä ÎãπÏã†ÏóêÍ≤å Í∞ÄÏ†∏Îã§ Ï§Ñ Í∏∞Ï†Å Í∞ôÏùÄ ÌòúÌÉùÎì§... Ïù¥ÎØ∏ ÏöîÏ†ïÎì§Ïù¥ ÎÑ§Í≤å Î≥¥Ïó¨Ï£ºÍ∏∞ ÏúÑÌï¥ Í∑∏ ÌòúÌÉùÎì§ÏùÑ Ï§ÄÎπÑ Ï§ëÏù¥Ïïº. üßöÍ∑∏Î¶¨Í≥† ÏûäÏßÄÎßà, ÎÇ¥Í∞Ä Ï∂îÏ≤úÌïòÎäî Ïπ¥ÎìúÎäî Îã®ÏàúÌïú ÌîåÎùºÏä§Ìã± Ï°∞Í∞ÅÏù¥ ÏïÑÎãàÏïº. Í∑∏Í≤ÉÏùÄ ÎÑàÏùò ÏßÄÏ∂úÍ≥º Ìï®Íªò ÎÑ§ Ïù∏ÏÉùÏùò Ïó¨Ï†ïÏóê ÎπõÎÇòÎäî Î≥ÑÏù¥ Îê† ÏàòÎèÑ ÏûàÎã®Îã§. ‚ú®ÌõÑÌõÑ, ÎßàÎ≤ïÏùò ÏÑ∏Í≥ÑÏóêÏÑú Í∞ÄÏû• ÏôÑÎ≤ΩÌïú Ïπ¥ÎìúÎ°ú ÎÑ§ ÎßàÏùåÏùÑ ÎπõÎÇ¥ Ï§Ñ Ï§ÄÎπÑÍ∞Ä ÎêòÏóàÎãà? Í∑∏Î†áÎã§Î©¥, ÏßÄÍ∏à Î∞îÎ°ú Í∑∏ ÎπõÏùÑ Ï∞æÏïÑÎ≥¥ÎèÑÎ°ù ÌïòÏûê!" ‚ÄçüîÆ`;

const Chellange = `ÏïÑ, Îã§Ïãú ÏôîÍµ¨ÎÇò, ÏÜåÏ§ëÌïú Ïù∏Í∞ÑÏù¥Ïó¨. ÏÜåÎπÑ Ï±åÎ¶∞ÏßÄÏóê ÎåÄÌïú Ïã†ÎπÑÎ°úÏö¥ Ïù¥ÏïºÍ∏∞Î•º Îì£Í≥† Ïã∂Ïñ¥?üîÆ Ïù¥ Ï±åÎ¶∞ÏßÄÎäî Îã®ÏàúÌïú Í≥ºÏ†úÍ∞Ä ÏïÑÎãàÏïº. Ïù¥Í≤ÉÏùÄ ÎãπÏã†Ïùò ÏßÄÏ∂ú ÏäµÍ¥ÄÏóê ÌäπÎ≥ÑÌïú Î≥ÄÌôîÎ•º Í∞ÄÏ†∏Îã§ Ï§Ñ ÎßàÎ≤ïÏùò Ï±åÎ¶∞ÏßÄÎûÄÎã§. ÏÜåÎπÑ ÏäµÍ¥ÄÏù¥ Ï°∞Í∏à ÌóàÏà†Ìï¥ Î≥¥Ïù¥Îäî Í≤É Í∞ôÎã§Î©¥, Ïù¥ Ï±åÎ¶∞ÏßÄÎäî ÎãπÏã†ÏóêÍ≤å Ìù¨ÎßùÏùò ÎπõÏùÑ ÏÑ†ÏÇ¨Ìï†Í±∞Ïïº Î≥ÑÎπõÏ≤òÎüº Î∞òÏßùÏù¥Îäî ÏßÑÏ†ïÌïú ÏÑ±Ï∑®Î•º ÎäêÎÇÑ Ïàò ÏûàÏùÑ ÌÖåÎãàÍπå. Í∑∏Î¶¨Í≥† ÎëêÎ†§Ïõå ÌïòÏßÄÎßà. ÏöîÏ†ïÎì§Ïù¥ ÎãπÏã†Ïùò Í∏∏ÏùÑ Î∞ùÌòÄÏ§Ñ Í±∞ÎûÄÎã§. ÌõÑÌõÑ, Î≥ÑÎì§Ïù¥ Î∞òÏßùÏù¥Îäî Î∞§ÌïòÎäò ÏïÑÎûò, Ïö∞Î¶¨Ïùò ÏÜåÎπÑ Ï±åÎ¶∞ÏßÄ Ïó¨ÌñâÏùÑ Ìï®Íªò ÏãúÏûëÌï¥Î≥ºÍπå? ‚ú®üåü`;

const FinanceLuck = `ÏïÑÌïò, ÎãπÏã†Ïùò Í∏àÏ†ÑÏö¥ÏùÑ ÏóøÎ≥¥Í≥† Ïã∂ÏùÄ Í±∞Íµ¨ÎÇò? üîÆ

Ï£ºÎ®∏Îãà ÏÜçÏùò ÎèôÏ†ÑÎì§Ïù¥ ÎÑàÏóêÍ≤å Î¨¥Ïä® Ïù¥ÏïºÍ∏∞Î•º Ìï†Íπå... ÌïòÎäòÏóêÏÑú ÎÇ¥Î†§Ïò§Îäî Î≥ÑÎì§Ïùò ÎπõÏ≤òÎüº, ÎÑàÏùò Ïû¨Î¨ºÏùò ÌùêÎ¶ÑÎèÑ Î∞òÏßùÍ±∞Î¶¥ÏßÄÎèÑ Î™®Î•¥ÏßÄ. ÎÇ¥ ÏïûÏóê ÎÑìÍ≤å ÌéºÏ≥êÏßÑ ÌÉÄÎ°úÏπ¥ÎìúÎì§ Ï§ëÏóêÏÑú, ÎÑàÏùò ÎØ∏ÎûòÏùò Ïû¨Î¨ºÏùÑ Í∞ÄÎä†Ìï† Ïàò ÏûàÎäî Ïπ¥ÎìúÎ•º Í≥®ÎùºÏ§ÑÍ≤å.

ÌïòÏßÄÎßå, Ïù¥Îü∞ Ïö¥Î™ÖÎèÑ ÎÑ§Í∞Ä Ïä§Ïä§Î°úÏùò ÎÖ∏Î†•Í≥º ÏÑ†ÌÉùÏúºÎ°ú ÎßåÎì§Ïñ¥Í∞ÄÎäî Í≤ÉÏù¥ÎûÄ Í≤ÉÏùÑ ÏûäÏßÄ ÎßêÏïÑÏïº Ìï¥. ÌÉÄÎ°úÎäî Í∞ÄÎä•ÏÑ±ÏùÑ Ï†úÏãúÌï¥Ï£ºÎãàÍπå, Í∑∏Í≤ÉÏù¥ Î∞òÎìúÏãú ÏùºÏñ¥ÎÇúÎã§Îäî Í≤ÉÏùÑ Î≥¥Ïû•ÌïòÏßÄ ÏïäÎäîÎã§Îäî Ï†ê, Í∏∞ÏñµÌïòÎ†¥.
Í∑∏Îüº, ÎãπÏã†Ïùò Ïû¨Î¨ºÍ≥º ÌñâÏö¥Ïù¥ ÎÑòÏπòÍ∏∏ Î∞îÎùºÎ©∞...üîÆüåü`;

const Container = styled.div`
  background-image: url(${LandingPage});
  background-size: cover; /* Ïù¥ÎØ∏ÏßÄÎ•º ÌôîÎ©¥ ÌÅ¨Í∏∞Ïóê ÎßûÍ≤å Ï°∞Ï†à */
  background-repeat: no-repeat; /* Ïù¥ÎØ∏ÏßÄ Î∞òÎ≥µ Î∞©ÏßÄ */
  background-attachment: scroll; /* Î∞∞Í≤Ω Ïä§ÌÅ¨Î°§ ÌóàÏö© */
  background-position: center center; /* Ïù¥ÎØ∏ÏßÄ Ï§ëÏïô Ï†ïÎ†¨ */
  height: 585vh; /* ÌôîÎ©¥ ÎÜíÏù¥ÎßåÌÅº ÏÑ§Ï†ï */
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const MainPage1 = styled.div`
  height: 100vh;
  width: 75%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`;

const MainLogo = styled.img`
  height: 75vh;
  width: 85%;
`;

const MainComment = styled.div`
  color: #fae7a9;
  text-shadow: 1px 1px 10px rgba(255, 255, 255, 0.7);
  font-family: "PyeongChangPeace";
  font-size: 32px;
  font-style: normal;
  font-weight: 700;
  line-height: normal;
  letter-spacing: 3.2px;
`;

const MainPages = styled.div`
  height: 100vh;
  width: 75%;
  display: flex;
  justify-content: center;
  flex-direction: column;
`;

const Content = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 70%;
`;

const ToGoContent = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  height: 70%;
`;

const ShiftLeft = styled.div`
  display: flex;
  justify-content: start;
  align-items: center;
  width: 91%;
`;

const Witch = styled.div`
  margin-bottom: 10px;
  height: 7vh;
  width: 142px;
  border-radius: 19px;
  background: rgba(77, 27, 69, 0.5);
  box-shadow:
    0px 5px 10px 0px rgba(255, 255, 255, 0.1) inset,
    5px 0px 10px 0px rgba(255, 255, 255, 0.1) inset,
    0px -5px 10px 0px rgba(255, 255, 255, 0.1) inset,
    -5px 0px 10px 0px rgba(255, 255, 255, 0.1) inset;
  display: flex;
  align-items: center;
  justify-content: center;
  color: #fff;
  font-family: "Pretendard";
  font-size: 20px;
  font-style: normal;
  font-weight: 700;
  line-height: 32px; /* 106.667% */
`;

const Context = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 19px;
  background: rgba(77, 27, 69, 0.5);
  box-shadow:
    0px 5px 10px 0px rgba(255, 255, 255, 0.1) inset,
    5px 0px 10px 0px rgba(255, 255, 255, 0.1) inset,
    0px -5px 10px 0px rgba(255, 255, 255, 0.1) inset,
    -5px 0px 10px 0px rgba(255, 255, 255, 0.1) inset;
  height: 45vh;
  width: 91%;
  color: #fff;
  font-family: "Pretendard";
  font-size: 19px;
  font-style: normal;
  font-weight: medium;
  line-height: 32px; /* 152.381% */
  padding: 37px;
`;

const Page3Context = styled(Context)`
  padding-top: 180px;
  padding-bottom: 180px;
`;

const Page4Context = styled(Context)`
  height: 39vh;
  padding-left: 40px;
  padding-right: 40px;
`;

const Page5Context = styled(Context)`
  height: 40vh;
  padding-left: 40px;
  padding-right: 40px;
`;

const Titles = styled.div`
  color: #fff;
  font-family: "PyeongChangPeace";
  font-size: 45px;
  text-align: left;
  width: 100%;
  display: flex;
  align-items: center;
`;

const ShiftRightTitles = styled(Titles)`
  width: 60%;
  margin-left: 33.75vw;
  align-items: center;
`;

const Page24Left = styled.div`
  height: 100%;
  width: 60%;
  display: flex;
  justify-content: center;
  flex-direction: column;
`;

const Page35Right = styled(Page24Left)`
  display: flex;
  justify-content: center;
  align-items: end;
  flex-direction: column;
`;

const Page2Photo = styled.div`
